INSERT INTO `jokes` (`joke_id`,`category`,`joke`,`source`) VALUES (1834,'KIDJOKES','Why did the chicken cross the road?\nTo get to the other side!','Postman');
INSERT INTO `jokes` (`joke_id`,`category`,`joke`,`source`) VALUES (1835,'KIDJOKES','Dirty Joke: The boy fell into the mud.\nClean Joke: He took a shower!','Postman');
INSERT INTO `jokes` (`joke_id`,`category`,`joke`,`source`) VALUES (1836,'DADJOKES','My wife is so negative. I remembered the car seat, the stroller, AND the diaper bag. Yet all she can talk about is how I forgot the baby.!','Postman');
INSERT INTO `jokes` (`joke_id`,`category`,`joke`,`source`) VALUES (1837,'DADJOKES','Q. Why did the scarecrow win an award?\nA. Because he was outstanding in his field.','Postman');
INSERT INTO `jokes` (`joke_id`,`category`,`joke`,`source`) VALUES (1838,'MOMJOKES','Don’t wake up mom! There are at least seven species who eat their young. Your mom may be one of them.','Postman');
