INSERT INTO `jokes` (`joke_id`,`category`,`joke`,`source`) VALUES (1840,'KNOCKKNOCK','Knock! Knock!\nWho’s there?\nSays.\nSays who?\nSays me, that’s who!','Postman');
INSERT INTO `jokes` (`joke_id`,`category`,`joke`,`source`) VALUES (1841,'TECHNOLOGY','Q. How did the programmer die in the shower?\nA. He read the shampoo bottle instructions: Lather. Rinse. Repeat.','Postman');
